import React from 'react';
import './App.css';
import Flexbox from './component/Flexbox';
import { useState } from 'react';

function App() {
  const [flexDirection, setFlexDirection] =useState();
  const [justifyContent, setJustifyContent] = useState('Anfangswert');
  const [alignItmes, setAlignItems] = useState('Anfangswert');
  return (
    <div className="App">
      <h1>Flexbox Konfigurator</h1>
      <fieldset>
        flex-direction:
        <input
       type= 'radio'id='row'name='flex-direction'value='row'
       onChange={() => {
         setFlexDirection('row');
       }
      }
      />
      <label htmlFor='row'>row</label>
      <input  type= 'radio'id='column'name='flex-direction'value='column'
      onChange={() =>{
        setFlexDirection('column');
      }}
      />
      <label htmlFor='column'>column</label>
      </fieldset>
      <fieldset>
        justify-content:
       <select id='justify-content'>
         <option value='flex-start'>flex-start</option>
         <option value='flex-end'>flex-end</option>
         <option value='center'>center</option>
         <option value='space-between'>space-between</option>
         <option value='space-around'>space-around</option>
       </select>
      </fieldset>
      <fieldset>
        alin-items:
       <select id='alin-items'>
         <option value='flex-start'>flex-start</option>
         <option value='flex-end'>flex-end</option>
         <option value='center'>center</option>
         <option value='stretch'>stretch</option>
         <option value='baseli'>space-around</option>
       </select>
      </fieldset>

    </div>
  );
}

export default App;
