import React from 'react';
import './App.css';
import Flexbox from './component/Flexbox';
import {useEffect, useState } from 'react';

function App() {
  const [flexDirection, setFlexDirection] =useState("row");
  const [justifyContent, setJustifyContent] = useState();
  const [alignItems, setAlignItems] = useState();
  const direction = localStorage.getItem("row");
  const justify = localStorage.getItem("flex-end");
  const align =localStorage.getItem("center")
  useEffect(()=>{
    localStorage.setItem("row",direction)
  },[flexDirection]);
  useEffect(()=>{
    localStorage.setItem("flex-e")
  },[flexDirection]);
  useEffect(()=>{
    localStorage.setItem("row",direction)
  },[flexDirection]);
  return (
    <div className="App">
      <h1>Flexbox Konfigurator</h1>
      <fieldset>
         flex-direction:
        <input
       type= 'radio'id='row'name='flex-direction'value='row'
       onChange={() => {
         setFlexDirection('row');
       }
      }
      />
      <label htmlFor='row'>row</label>
      <input  type= 'radio'id='column'name='flex-direction'value='column'
      onChange={() =>{
        setFlexDirection('column');
      }}
      />
      <label htmlFor='column'>column</label>
      </fieldset>
       <fieldset id='justify-content'>
        justify-content:
       <select id='justify-content' onChange={(e)=>{
         setJustifyContent(e.target.value);

       }}>
         <option value='flex-start'>flex-start</option>
         <option value='flex-end'>flex-end</option>
         <option value='center'>center</option>
         <option value='space-between'>space-between</option>
         <option value='space-around'>space-around</option>
       </select>
      </fieldset>
      <fieldset id='align_Items'>
        alin-items:
       <select id='align-items' onChange={(e)=>{
         setAlignItems(e.target.value);
       }
      }>
         <option value='flex-start'>flex-start</option>
         <option value='flex-end'>flex-end</option>
         <option value='center'>center</option>
         <option value='stretch'>stretch</option>
         <option value='baseline'>baseline</option>
       </select>
      </fieldset>
      <div className='color-container' style={{
        flexDirection: flexDirection,
        justifyContent: justifyContent,
        alignItems: alignItems,
      }}>


    <Flexbox color='red'></Flexbox>
    <Flexbox color='yellow'></Flexbox>
    <Flexbox color='green'></Flexbox>
    <Flexbox color='blue'></Flexbox>
    <Flexbox color='purple'></Flexbox>
     </div>

    </div>
  );
}

export default App;
